.zpspbt-outer-wrapper {
height: 100%;
font-family: 'Trebuchet MS';
display: flex;
align-items: flex-start;
background: rgba(0, 0, 0, 0.6);
font-size: 14px;
}
.zpspbt-inner-wrapper {
background: #fff;
width: 100%;
display: flex;
justify-content: center;
align-items: center;
padding: 20px;
box-shadow: 0 0 7px #cfcfcf;
}
.zpspbt-heading {
display: flex;
font-size: 2em;
vertical-align: top;
margin: 0;
}
.zpspbt-content{
font-size: 1em;
padding: 0 0 0 50px;
line-height: 22px;
}
.zpspbt-form-block {
display: flex;
justify-content: center;
line-height: 0;
margin-left: 50px;
}
.zpspbt-inuput-field {
height: 20px;
border: 1px solid #cbcbcb;
border-radius: 5px;
}
.zpspbt-close-button {
position: absolute;
top: 1.5%;
right: 5%;
cursor: pointer;
display: flex;
justify-content: center;
align-items: center;
font-size: 20px;
width: 1.25em;
height: 1.25em;
line-height: 12px;
color: #000;
background: #dcdde0;
border-radius: 50px;
}
.zpspbt-default-text-box {
box-sizing: border-box;
border: 1px solid #cbcbcb;
border-radius: 3px;
padding: 10px;
color: #929292;
resize: none;
font-size: 1em;
height: 90px;
background: #fff;
font-family: 'Trebuchet MS';
}
.zpspbt-default-input-box {
box-sizing: border-box;
border: 1px solid #cbcbcb;
border-radius: 3px;
color: #929292;
resize: none;
font-size: 1em;
padding: 5px 10px;
height: 36px;
background: #fff;
font-family: 'Trebuchet MS';
}
.zpspbt-default-button {
background: #dcdddf;
color: #a5aaaf;
border: none;
padding: 0px 40px;
font-size: 14px;
height: 40px;
border-radius: 5px;
cursor: pointer;
display: inline-block;
font-family: 'Trebuchet MS';
}
.zpspbt-default-text-box{
box-sizing: border-box;
border: 1px solid #cbcbcb;
border-radius: 3px;
padding: 10px;
color: #929292;
resize: none;
font-size: 12px;
height: 90px;
background: #fff;
font-family: 'Trebuchet MS';
}
.zpspbt-default-input-box {
box-sizing: border-box;
border: 1px solid #cbcbcb;
border-radius: 3px;
color: #929292;
resize: none;
font-size: 1em;
padding: 5px 10px;
height: 36px;
background: #fff;
font-family: 'Trebuchet MS';
}
.zpspbt-close-icon {
color: #a5aaaf;
}
@media(min-width: 320px) and (max-width: 767px) {
.zpspbt-outer-wrapper {
font-size: 13px;
}
.zpspbt-inner-wrapper {
justify-content: center;
flex-wrap: wrap;
}
.zpspbt-heading {
flex: 0 0 100%;
margin: 15px 0 0;
}
.zpspbt-form-block {
flex: 0 0 100%;
}
.zpspbt-content{
padding: 0;
}
}
